mapper.update:
UPDATE `status`
SET status=0, department_id = (
    SELECT volunteer_department
    FROM (
             SELECT volunteer_department
             FROM volunteer
             WHERE `level` > (
                 SELECT department_id
                 FROM `status`
                 WHERE id = #{id}
             ) AND id =#{id}
                 LIMIT 1
         ) AS subquery
)
WHERE id = #{id};
